import React, { useState } from "react";
import styled, { createGlobalStyle, keyframes } from "styled-components";
import { FiUploadCloud, FiDownload, FiCheckCircle } from "react-icons/fi";
import FileUpload from "./components/FileUpload";
import FeedbackDisplay from "./components/FeedbackDisplay";
import { uploadResume } from "./api";

const GlobalStyle = createGlobalStyle`
  body {
    font-family: 'Inter', sans-serif;
    background: linear-gradient(120deg, #f8fafc 0%, #e0e7ff 100%);
    min-height: 100vh;
    margin: 0;
  }
`;

const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: none; }
`;

const Container = styled.div`
  max-width: 540px;
  margin: 3rem auto;
  background: #fff;
  border-radius: 2rem;
  box-shadow: 0 8px 32px rgba(80, 80, 180, 0.10);
  padding: 2.5rem 2rem 2rem 2rem;
  animation: ${fadeIn} 0.8s cubic-bezier(.4,0,.2,1);
  position: relative;
`;

const Header = styled.header`
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-bottom: 2rem;
`;

const Mascot = styled.div`
  font-size: 2.5rem;
  filter: drop-shadow(0 2px 8px #e0e7ff);
`;

const Title = styled.h1`
  font-size: 2rem;
  font-weight: 800;
  color: #3730a3;
  margin: 0;
`;

const Subtitle = styled.p`
  color: #6366f1;
  font-weight: 600;
  margin: 0 0 1.5rem 0;
`;

const UploadSection = styled.section`
  margin-bottom: 2rem;
`;

const LoadingSpinner = styled.div`
  border: 4px solid #e0e7ff;
  border-top: 4px solid #6366f1;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  animation: spin 1s linear infinite;
  margin: 2rem auto;
  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const Success = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  color: #22c55e;
  font-weight: 600;
  margin-top: 1rem;
`;

function App() {
  const [feedback, setFeedback] = useState(null);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  const handleFileSelected = async (file) => {
    setLoading(true);
    setFeedback(null);
    setSuccess(false);
    try {
      const result = await uploadResume(file);
      setFeedback(result);
      setSuccess(true);
    } catch (err) {
      setFeedback({ error: "Failed to analyze resume." });
    }
    setLoading(false);
  };

  const handleDownload = () => {
    if (!feedback) return;
    const text = [
      `Resume Score: ${feedback.score || "N/A"}`,
      "\nSuggested Improvements:",
      ...(feedback.improvements || []).map((i) => `- ${i}`),
      "\nSkill Match:",
      feedback.skill_match || "",
      "\nComments:",
      feedback.comments || ""
    ].join("\n");
    const blob = new Blob([text], { type: "text/plain" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "resume_feedback.txt";
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <>
      <GlobalStyle />
      <Container>
        <Header>
          <Mascot>🦉</Mascot>
          <div>
            <Title>AI Resume Reviewer</Title>
            <Subtitle>Get instant, actionable feedback on your resume.</Subtitle>
          </div>
        </Header>
        <UploadSection>
          <FileUpload onFileSelected={handleFileSelected} />
        </UploadSection>
        {loading && <LoadingSpinner />}
        {success && (
          <Success>
            <FiCheckCircle size={22} /> Resume analyzed!
          </Success>
        )}
        <FeedbackDisplay feedback={feedback} onDownload={handleDownload} />
      </Container>
    </>
  );
}

export default App;
